import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = ({ setUser }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const res = await axios.post('http://localhost:5000/login', { username, password });
      setUser(res.data.user);
      if (res.data.user.role === 'admin') navigate('/admin');
      else navigate('/user');
    } catch (err) {
      alert('Invalid credentials or Database Connection Error');
      console.error(err);
    }
  };

  return (
    <div style={{ padding: '40px', maxWidth: '400px', margin: 'auto' }}>
      <h2>Login</h2>
      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <input style={{padding: '10px'}} placeholder="Username" onChange={e => setUsername(e.target.value)} />
        <input style={{padding: '10px'}} type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} />
        <button style={{padding: '10px', background: '#007bff', color: 'white', border: 'none'}} onClick={handleLogin}>Login</button>
      </div>
      <p>Try: admin/admin123 or user1/user123</p>
    </div>
  );
};

export default Login;
