import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserDashboard = ({ user }) => {
  const [tasks, setTasks] = useState([]);
  const [cancelReason, setCancelReason] = useState('');
  const [selectedTask, setSelectedTask] = useState(null);

  useEffect(() => { fetchTasks(); }, []);

  const fetchTasks = async () => {
    const res = await axios.get(`http://localhost:5000/user/${user.id}/tasks`);
    setTasks(res.data);
  };

  const handleCancel = async () => {
    if (!selectedTask) return;
    await axios.put(`http://localhost:5000/tasks/${selectedTask}/cancel`, { reason: cancelReason });
    setSelectedTask(null);
    setCancelReason('');
    fetchTasks();
  };

  const handleComplete = async (id) => {
    await axios.put(`http://localhost:5000/tasks/${id}/complete`);
    fetchTasks();
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome, {user.username}</h1>
      <h3>My Tasks</h3>
      
      <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead style={{ background: '#e0e0e0' }}>
          <tr>
            <th>Task</th><th>Project</th><th>Difficulty</th><th>Status</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map(task => (
            <tr key={task.id}>
              <td>{task.title}</td>
              <td>{task.project_name}</td>
              <td><b>{task.difficulty}</b></td>
              <td>{task.status}</td>
              <td>
                {task.status === 'active' && (
                  <>
                    <button onClick={() => handleComplete(task.id)} style={{marginRight:'10px', background: '#28a745', color: 'white', border: 'none', padding: '5px 10px'}}>Complete</button>
                    <button onClick={() => setSelectedTask(task.id)} style={{background: '#dc3545', color: 'white', border: 'none', padding: '5px 10px'}}>Cancel</button>
                  </>
                )}
                {task.status === 'completed' && <span>✅ Forwarded to Admin</span>}
                {task.status === 'cancelled' && <span>❌ Forwarded to Admin</span>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedTask && (
        <div style={{
          position: 'fixed', top: '0', left: '0', width: '100%', height: '100%',
          backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', justifyContent: 'center', alignItems: 'center'
        }}>
          <div style={{ background: 'white', padding: '20px', borderRadius: '5px', width: '300px' }}>
            <h4>Why are you cancelling?</h4>
            <textarea style={{width: '90%'}} onChange={e => setCancelReason(e.target.value)} rows="3" />
            <div style={{marginTop: '10px', display: 'flex', justifyContent: 'space-between'}}>
              <button onClick={handleCancel}>Submit</button>
              <button onClick={() => setSelectedTask(null)}>Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
