import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);
  
  const [newProject, setNewProject] = useState('');
  const [newTask, setNewTask] = useState({ title: '', difficulty: 'Medium', project_id: '' });

  useEffect(() => { fetchData(); }, []);

  const fetchData = async () => {
    const t = await axios.get('http://localhost:5000/admin/tasks');
    const p = await axios.get('http://localhost:5000/projects');
    const u = await axios.get('http://localhost:5000/users');
    setTasks(t.data);
    setProjects(p.data);
    setUsers(u.data);
  };

  const createProject = async () => {
    if(!newProject) return;
    await axios.post('http://localhost:5000/projects', { name: newProject });
    setNewProject('');
    fetchData();
  };

  const createTask = async () => {
    if(!newTask.title || !newTask.project_id) return;
    await axios.post('http://localhost:5000/tasks', newTask);
    setNewTask({ ...newTask, title: '' });
    fetchData();
  };

  const assignTask = async (taskId, userId) => {
    await axios.put(`http://localhost:5000/tasks/${taskId}/assign`, { userId });
    fetchData();
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Admin Dashboard</h1>
      
      <div style={{ display: 'flex', gap: '20px', marginBottom: '20px' }}>
        <div style={{ border: '1px solid #ccc', padding: '15px' }}>
          <h3>Create Project</h3>
          <input value={newProject} placeholder="Project Name" onChange={e => setNewProject(e.target.value)} />
          <button onClick={createProject}>Create</button>
        </div>

        <div style={{ border: '1px solid #ccc', padding: '15px' }}>
          <h3>Create Task</h3>
          <input value={newTask.title} placeholder="Task Title" onChange={e => setNewTask({...newTask, title: e.target.value})} />
          <select onChange={e => setNewTask({...newTask, difficulty: e.target.value})}>
            <option value="Hard">Hard</option>
            <option value="Medium">Medium</option>
            <option value="Easy">Easy</option>
          </select>
          <select onChange={e => setNewTask({...newTask, project_id: e.target.value})}>
            <option value="">Select Project</option>
            {projects.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
          </select>
          <button onClick={createTask}>Create Task</button>
        </div>
      </div>

      <h3>All Tasks (Ordered by Difficulty)</h3>
      <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead style={{ background: '#f0f0f0' }}>
          <tr>
            <th>Task</th><th>Project</th><th>Difficulty</th><th>Status</th><th>Assigned To</th><th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map(task => (
            <tr key={task.id} style={{ backgroundColor: task.status === 'cancelled' ? '#fff0f0' : 'white' }}>
              <td>{task.title}</td>
              <td>{task.project_name}</td>
              <td style={{ color: task.difficulty === 'Hard' ? 'red' : 'black' }}><b>{task.difficulty}</b></td>
              <td>
                {task.status.toUpperCase()}
                {task.status === 'cancelled' && <div style={{color:'red', fontSize:'0.8em'}}>Reason: {task.cancel_reason}</div>}
              </td>
              <td>{task.assigned_user || 'Unassigned'}</td>
              <td>
                <select onChange={(e) => assignTask(task.id, e.target.value)} defaultValue="">
                  <option value="" disabled>Assign / Reassign</option>
                  {users.map(u => <option key={u.id} value={u.id}>{u.username}</option>)}
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
