import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);
  
  const [newProject, setNewProject] = useState('');
  const [newTask, setNewTask] = useState({ title: '', difficulty: 'Medium', project_id: '' });

  useEffect(() => { fetchData(); }, []);

  const fetchData = async () => {
    const t = await axios.get('http://localhost:5000/admin/tasks');
    const p = await axios.get('http://localhost:5000/projects');
    const u = await axios.get('http://localhost:5000/users');
    setTasks(t.data);
    setProjects(p.data);
    setUsers(u.data);
  };

  const createProject = async () => {
    if(!newProject) return;
    await axios.post('http://localhost:5000/projects', { name: newProject });
    setNewProject('');
    fetchData();
  };

  const createTask = async () => {
    if(!newTask.title || !newTask.project_id) return;
    await axios.post('http://localhost:5000/tasks', newTask);
    setNewTask({ ...newTask, title: '' });
    fetchData();
  };

  const assignTask = async (taskId, userId) => {
    await axios.put(`http://localhost:5000/tasks/${taskId}/assign`, { userId });
    fetchData();
  };

  return (
    <div className="container">
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1>Admin Dashboard</h1>
        <button className="btn-primary" onClick={() => window.location.href='/'}>Logout</button>
      </header>
      
      <div className="dashboard-grid">
        {/* Project Card */}
        <div className="card">
          <h3>Create New Project</h3>
          <input value={newProject} placeholder="Project Name (e.g., Website Redesign)" onChange={e => setNewProject(e.target.value)} />
          <button className="btn-primary" style={{marginTop:'10px'}} onClick={createProject}>+ Add Project</button>
        </div>

        {/* Task Card */}
        <div className="card">
          <h3>Create New Task</h3>
          <input value={newTask.title} placeholder="Task Title" onChange={e => setNewTask({...newTask, title: e.target.value})} />
          <div style={{ display: 'flex', gap: '10px' }}>
            <select style={{flex:1}} onChange={e => setNewTask({...newTask, difficulty: e.target.value})}>
              <option value="Hard">Hard</option>
              <option value="Medium">Medium</option>
              <option value="Easy">Easy</option>
            </select>
            <select style={{flex:1}} onChange={e => setNewTask({...newTask, project_id: e.target.value})}>
              <option value="">Select Project</option>
              {projects.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
            </select>
          </div>
          <button className="btn-primary" style={{marginTop:'10px'}} onClick={createTask}>+ Add Task</button>
        </div>
      </div>

      <h3>All Tasks</h3>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Task</th>
              <th>Project</th>
              <th>Difficulty</th>
              <th>Status</th>
              <th>Assigned To</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map(task => (
              <tr key={task.id} className={`status-${task.status}`}>
                <td data-label="Task">{task.title}</td>
                <td data-label="Project">{task.project_name}</td>
                <td data-label="Difficulty"><span className={`badge badge-${task.difficulty}`}>{task.difficulty}</span></td>
                <td data-label="Status">
                  <span style={{textTransform:'capitalize', fontWeight:'bold'}}>{task.status}</span>
                  {task.status === 'cancelled' && <div style={{color:'#c62828', fontSize:'0.85rem', marginTop:'5px'}}>Reason: {task.cancel_reason}</div>}
                </td>
                <td data-label="Assigned To">{task.assigned_user || <span style={{color:'#999'}}>Unassigned</span>}</td>
                <td data-label="Action">
                  <select 
                    style={{ marginBottom:0, padding:'5px' }} 
                    onChange={(e) => assignTask(task.id, e.target.value)} 
                    value={task.assigned_to || ""}
                  >
                    <option value="" disabled>Select User</option>
                    {users.map(u => <option key={u.id} value={u.id}>{u.username}</option>)}
                  </select>
                </td>
              </tr>
            ))}
            {tasks.length === 0 && <tr><td colSpan="6" style={{textAlign:'center', padding:'30px'}}>No tasks found. Create one above!</td></tr>}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
