import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserDashboard = ({ user }) => {
  const [tasks, setTasks] = useState([]);
  const [cancelReason, setCancelReason] = useState('');
  const [selectedTask, setSelectedTask] = useState(null);

  useEffect(() => { fetchTasks(); }, []);

  const fetchTasks = async () => {
    const res = await axios.get(`http://localhost:5000/user/${user.id}/tasks`);
    setTasks(res.data);
  };

  const handleCancel = async () => {
    if (!selectedTask) return;
    await axios.put(`http://localhost:5000/tasks/${selectedTask}/cancel`, { reason: cancelReason });
    setSelectedTask(null);
    setCancelReason('');
    fetchTasks();
  };

  const handleComplete = async (id) => {
    await axios.put(`http://localhost:5000/tasks/${id}/complete`);
    fetchTasks();
  };

  return (
    <div className="container">
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div>
            <h1>Hello, {user.username} üëã</h1>
            <p style={{color:'#7f8c8d'}}>Here are your assigned tasks.</p>
        </div>
        <button className="btn-primary" onClick={() => window.location.href='/'}>Logout</button>
      </header>
      
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Task</th>
              <th>Project</th>
              <th>Difficulty</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map(task => (
              <tr key={task.id} className={`status-${task.status}`}>
                <td data-label="Task" style={{fontWeight:'500'}}>{task.title}</td>
                <td data-label="Project">{task.project_name}</td>
                <td data-label="Difficulty"><span className={`badge badge-${task.difficulty}`}>{task.difficulty}</span></td>
                <td data-label="Status" style={{textTransform:'capitalize'}}>{task.status}</td>
                <td data-label="Actions">
                  {(task.status === 'active' || task.status === 'forwarded') && (
                    <div style={{display:'flex', gap:'10px', flexWrap:'wrap'}}>
                      <button className="btn-success btn-sm" onClick={() => handleComplete(task.id)}>‚úî Complete</button>
                      <button className="btn-danger btn-sm" onClick={() => setSelectedTask(task.id)}>‚úñ Cancel</button>
                    </div>
                  )}
                  {task.status === 'completed' && <span className="badge" style={{background:'#e8f5e9', color:'green'}}>‚úÖ Sent to Admin</span>}
                  {task.status === 'cancelled' && <span className="badge" style={{background:'#ffebee', color:'red'}}>‚ùå Sent to Admin</span>}
                </td>
              </tr>
            ))}
            {tasks.length === 0 && <tr><td colSpan="5" style={{textAlign:'center', padding:'30px'}}>Good job! No active tasks assigned to you.</td></tr>}
          </tbody>
        </table>
      </div>

      {selectedTask && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3 style={{marginTop:0}}>Reason for Cancellation</h3>
            <p style={{fontSize:'0.9rem', color:'#666'}}>Please explain why you cannot complete this task.</p>
            <textarea 
                style={{width: '100%', minHeight: '80px', padding:'10px', border:'1px solid #ddd', borderRadius:'4px'}} 
                placeholder="Type your reason here..."
                onChange={e => setCancelReason(e.target.value)} 
            />
            <div style={{marginTop: '15px', display: 'flex', justifyContent: 'flex-end', gap:'10px'}}>
              <button style={{background:'transparent', color:'#666'}} onClick={() => setSelectedTask(null)}>Close</button>
              <button className="btn-primary" onClick={handleCancel}>Submit Reason</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
