import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = ({ setUser }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const res = await axios.post('http://localhost:5000/login', { username, password });
      setUser(res.data.user);
      if (res.data.user.role === 'admin') navigate('/admin');
      else navigate('/user');
    } catch (err) {
      alert('Invalid credentials or Server Error');
    }
  };

  return (
    <div style={{ height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', background: '#f4f7f6' }}>
      <div className="card" style={{ width: '100%', maxWidth: '400px', textAlign: 'center' }}>
        <h2 style={{ color: '#4a90e2' }}>Task Manager</h2>
        <p style={{ color: '#7f8c8d', marginBottom: '20px' }}>Please sign in to continue</p>
        
        <div className="form-group">
          <input placeholder="Username" onChange={e => setUsername(e.target.value)} />
        </div>
        <div className="form-group">
          <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} />
        </div>
        <button className="btn-primary" style={{ width: '100%' }} onClick={handleLogin}>Login</button>
        
        <div style={{ marginTop: '20px', fontSize: '0.8rem', color: '#999', textAlign: 'left', background: '#f9f9f9', padding: '10px' }}>
          <strong>Demo Credentials:</strong><br/>
          Admin: <code>admin / admin123</code><br/>
          User: <code>user1 / user123</code>
        </div>
      </div>
    </div>
  );
};

export default Login;
